import React, { useEffect, useState } from 'react';

import Tasks from './components/Tasks/Tasks';
import NewTask from './components/NewTask/NewTask';
import useHttp from './hooks/use-http';

function App() {
	const [tasks, setTasks] = useState([]);

	//transform data from backend to match what we need to display in frontend
	const transformTask = (tasksObj) => {
		const loadedTasks = [];

		for (const taskKey in tasksObj) {
			loadedTasks.push({ id: taskKey, text: tasksObj[taskKey].text });
		}

		setTasks(loadedTasks);
	};

	const httpData = useHttp(
		{ url: 'https://react-http-30b70-default-rtdb.firebaseio.com/tasks.json' },
		transformTask
	);
	const { isLoading, error, sendRequest } = httpData;
	/**ABOVE TO LINES CAN ALSO BE WRITTEN LIKE THIS:
	 * const {isLoading, error, sendRequest} = useHttp({url: 'https://react-http-30b70-default-rtdb.firebaseio.com/tasks.json' }, transformTask);
	 */
	const fetchTasks = async (taskText) => {
		setIsLoading(true);
		setError(null);
		try {
			const response = await fetch(
				'https://react-http-30b70-default-rtdb.firebaseio.com/tasks.json'
			);

			if (!response.ok) {
				throw new Error('Request failed!');
			}

			const data = await response.json();
		} catch (err) {
			setError(err.message || 'Something went wrong!');
		}

		setIsLoading(false);
	};

	useEffect(() => {
		fetchTasks();
	}, []);

	const taskAddHandler = (task) => {
		setTasks((prevTasks) => prevTasks.concat(task));
	};

	return (
		<React.Fragment>
			<NewTask onAddTask={taskAddHandler} />
			<Tasks
				items={tasks}
				loading={isLoading}
				error={error}
				onFetch={fetchTasks}
			/>
		</React.Fragment>
	);
}

export default App;
